    const handleSubmit = (e) => {
      e.preventDefault();
      // Send POST/PUT request to Flask backend to add or edit task
    };

    return (
      <form onSubmit={handleSubmit}>
        <input type="text" name="task_name" placeholder="Task Name" value={taskData.task_name || ''} onChange={console.log("changed")} />
        <select name="task_type" value={taskData.task_type || ''} onChange={console.log("changed")}>
